return items.map(item => {
  const rawHtml = item.json.data
    || item.json.body
    || (item.binary?.data?.toString('utf8'))
    || '';

  // Meta‚Äêtag helper
  function extractMeta(prop, attr = 'property') {
    const re = new RegExp(
      `<meta[^>]*${attr}=["']${prop}["'][^>]*content=["']([^"']+)["']`,
      'i'
    );
    const m = rawHtml.match(re);
    return m ? m[1].trim() : '';
  }

  // COMPANY (unchanged from before) ‚Ä¶
  const companyName =
    extractMeta('og:site_name') ||
    rawHtml.match(
      /<a[^>]*class=["'][^"']*(?:topcard__org-name-link)[^"']*["'][^>]*>([^<]+)<\/a>/i
    )?.[1].trim() ||
    rawHtml.match(
      /<span[^>]*class=["'][^"']*(?:jobs-unified-top-card__company-name)[^"']*["'][^>]*>([^<]+)<\/span>/i
    )?.[1].trim() ||
    '';

  // 1. Grab the raw OG title (or fallback h1)
  let rawTitle =
    extractMeta('og:title') ||
    extractMeta('twitter:title', 'name') ||
    (rawHtml.match(/<h1[^>]*>([\s\S]*?)<\/h1>/i)?.[1].replace(/<[^>]+>/g,' ').trim()) ||
    '';

  // 2. If it‚Äôs the LinkedIn pattern "X hiring Y in Z | LinkedIn",
  //    pull Y; otherwise leave as-is.
  const m = rawTitle.match(/hiring\s+(.+?)\s+in\s+.*\|\s*LinkedIn$/i);
  const jobTitle = m ? m[1] : rawTitle;

  // DESCRIPTION (unchanged) ‚Ä¶
  const descClasses = ['job-description','description','jd-block','description__text'];
  let jobDescription = '';
  for (const cls of descClasses) {
    const re = new RegExp(
      `<div[^>]*class=["'][^"']*${cls}[^"']*["'][^>]*>([\\s\\S]*?)<\\/div>`,
      'i'
    );
    const mm = rawHtml.match(re);
    if (mm?.[1]) { jobDescription = mm[1]; break; }
  }
  jobDescription = jobDescription
    .replace(/<[^>]+>/g,' ')
    .replace(/\s+/g,' ')
    .trim()
    .substring(0,10000);

  return { json: { companyName, jobTitle, jobDescription } };
});

const items = $input.all();
const results = [];

for (const item of items) {
  const raw = item.json?.output;

  if (typeof raw === 'string') {
    try {
      const cleaned = raw.replace(/^```json\s*|\s*```$/g, "").trim();
      const safeClean = cleaned.endsWith("}") ? cleaned : cleaned + "}";
      const parsed = JSON.parse(safeClean);

      // Push each parsed object as a whole item
      results.push(parsed);

    } catch (e) {
      // Skip malformed JSON silently
      continue;
    }
  }
}

return results;

